import React from "react";

const ShowSummary = ({ show, onBookTicket }) => {
  const handleBookTicket = () => {
    onBookTicket(show);
  };

  return (
    <div>
      <h2>Show Summary</h2>
      <h3>{show.show.name}</h3>
      <p>{show.show.summary}</p>
      <button onClick={handleBookTicket}>Book Ticket</button>
    </div>
  );
};

export default ShowSummary;
