import React from "react";
import { Link } from "react-router-dom";

const ShowSummary = ({ show }) => {
  return (
    <div>
      <h2>Show Summary</h2>
      <h3>{show.show.name}</h3>
      <p>{show.show.summary}</p>
      <Link to={`/booking/${show.show.id}`}>
        <button>Book Ticket</button>
      </Link>
    </div>
  );
};

export default ShowSummary;
