import React, { useState } from "react";
import ShowList from "./components/ShowList";
import ShowSummary from "./components/ShowSummary";
import TicketBookingForm from "./TicketBookingForm";

const App = () => {
  const [selectedShow, setSelectedShow] = useState(null);
  const [bookingDetails, setBookingDetails] = useState(null);

  const handleSelectShow = (show) => {
    setSelectedShow(show);
  };

  const handleBookTicket = (show) => {
    setSelectedShow(show);
  };

  const handleTicketBooked = (ticketDetails) => {
    localStorage.setItem("ticketDetails", JSON.stringify(ticketDetails));
    setBookingDetails(ticketDetails);
  };

  const handleCancelBooking = () => {
    localStorage.removeItem("ticketDetails");
    setBookingDetails(null);
  };

  return (
    <div>
      <h1>TV Show Booking App</h1>
      {bookingDetails ? (
        <div>
          <h2>Booking Confirmation</h2>
          <p>You have successfully booked a ticket for:</p>
          <p>Show: {bookingDetails.showName}</p>
          <p>Summary: {bookingDetails.showSummary}</p>
          <p>Name: {bookingDetails.name}</p>
          <p>Email: {bookingDetails.email}</p>
          <button onClick={handleCancelBooking}>Cancel Booking</button>
        </div>
      ) : (
        <div>
          {selectedShow ? (
            <TicketBookingForm
              show={selectedShow}
              onTicketBooked={handleTicketBooked}
            />
          ) : (
            <ShowList onSelectShow={handleSelectShow} />
          )}
        </div>
      )}
    </div>
  );
};

export default App;
